        <div style="padding-top: 5px">
    </br>
<div class="input-group">
    <input id="13072349269886558537" type="number" class="form-control" placeholder="Numeric answer">
</div>


        </div>
      </div>
      <div id='13072349269886558537_message' style="padding-bottom: 15px"></div>
    </div>
  </div>
